mapper.insert:
INSERT INTO CHAT_ROOM VALUES ( SEQ_CR_NO.NEXTVAL, #{title}, DEFAULT, #{userNo}
)
SELECT SEQ_CR_NO.CURRVAL FROM DUAL

mapper.delete:

DELETE FROM CHAT_ROOM_JOIN
WHERE CHAT_ROOM_NO = #{chatRoomNo}
	AND USER_NO = #{userNo}

mapper.select:
SELECT COUNT(*)
FROM CHAT_ROOM_JOIN
WHERE CHAT_ROOM_NO = #{chatRoomNo}

mapper.update:
UPDATE CHAT_ROOM 
SET STATUS = 'N' 
WHERE CHAT_ROOM_NO = #{chatRoomNo}
